ramming","ownerLogin":"gokul-chandrasekar","currentUserCanPush":false,"isFork":false,"isEmpty":false,"createdAt":"2023-07-10T21:59:21.000+05:30","ownerAvatar":"https://avatars.githubusercontent.com/u/113445140?v=4","public":true,"private":false,"isOrgOwned":false},"symbolsExpanded":false,"treeExpanded":true,"refInfo":{"name":"main","listCacheKey":"v0:1691938061.0","canEdit":true,"refType":"branch","currentOid":"8903211aceb994e588d3984da88f6a675bda04fb"},"path":"R lab programs/Ex 30. logistic regression.R","currentUser":{"id":100275995,"login":"sakthivelg007","userEmail":"svel56937@gmail.com"},"blob":{"rawLines":["# Load required libraries\r","library(dplyr)\r","library(caTools)  # For splitting the data\r","library(glmnet)   # For logistic regression\r","library(caret)    # For confusion matrix\r","\r","# Load the Iris dataset\r","data(\"iris\")\r","\r","# Selecting required columns\r","iris_subset <- iris %>% select(Species, Petal.Length, Petal.Width)\r","\r","# Set a random seed for reproducibility\r","set.seed(123)\r","\r","# Splitting the data into training and test sets (80% - 20%)\r","split <- sample.split(iris_subset$Species, SplitRatio = 0.8)\r","train_data <- subset(iris_subset, split == TRUE)\r","test_data <- subset(iris_subset, split == FALSE)\r","\r","# Create a logistic regression model\r","log_model <- glm(Species ~ Petal.Length + Petal.Width, data = train_data, family = \"binomial\")\r","\r","# Predict probabilities using the test data\r","test_probs <- predict(log_model, newdata = test_data, type = \"response\")\r","\r","# Convert probabilities to predicted classes\r","predicted_classes <- ifelse(test_probs > 0.5, \"versicolor\", \"setosa\")\r","\r","# Create a confusion matrix\r","confusion <- confusionMatrix(predicted_classes, test_data$Species)\r","\r","# Display the confusion matrix\r","print(confusion)\r"],"stylingDirectives":[[{"start":0,"end":25,"cssClass":"pl-c"},{"start":0,"end":1,"cssClass":"pl-c"}],[{"start":8,"end":13,"cssClass":"pl-smi"}