ownerLogin":"gokul-chandrasekar","currentUserCanPush":false,"isFork":false,"isEmpty":false,"createdAt":"2023-07-10T21:59:21.000+05:30","ownerAvatar":"https://avatars.githubusercontent.com/u/113445140?v=4","public":true,"private":false,"isOrgOwned":false},"symbolsExpanded":false,"treeExpanded":true,"refInfo":{"name":"main","listCacheKey":"v0:1691938061.0","canEdit":true,"refType":"branch","currentOid":"8903211aceb994e588d3984da88f6a675bda04fb"},"path":"R lab programs/Ex 34. ozone and solar regression.R","currentUser":{"id":100275995,"login":"sakthivelg007","userEmail":"svel56937@gmail.com"},"blob":{"rawLines":["# Load the required library\r","library(ggplot2)\r","\r","# Load the airquality dataset\r","data(airquality)\r","\r","# (i) Find and handle missing values\r","na_percentage_threshold <- 0.1\r","\r","# Calculate the percentage of missing values for each feature\r","missing_percent <- colMeans(is.na(airquality))\r","\r","# Replace missing values with mean if the missing percentage is less than 10%, otherwise drop them\r","for (col in colnames(airquality)) {\r","  if (missing_percent[col] < na_percentage_threshold) {\r","    airquality[is.na(airquality[[col]]), col] <- mean(airquality[[col]], na.rm = TRUE)\r","  } else {\r","    airquality <- airquality[complete.cases(airquality), ]\r","  }\r","}\r","\r","# (ii) Apply linear regression using Least Squares Method on \"Ozone\" and \"Solar.R\"\r","lm_model <- lm(Ozone ~ Solar.R, data = airquality)\r","summary(lm_model)\r","\r","# (iii) Plot Scatter plot between Ozone and Solar with regression line\r","ggplot(airquality, aes(x = Solar.R, y = Ozone)) +\r","  geom_point() +\r","  geom_smooth(method = \"lm\", se = FALSE, color = \"red\") +\r","  labs(title = \"Scatter Plot of Ozone vs. Solar.R with Regression Line\")\r"],"stylingDirectives":[[{"start":0,"end":27,"cssClass":"pl-c"},{"start":0,"end":1,"cssClass":"pl-c"}],[{"start":8,"end":15,"cssClass":"pl-smi"}],[],[{"start":0,"end":29,"cssClass":"pl-c"},{"start":0,"end"