amming","ownerLogin":"gokul-chandrasekar","currentUserCanPush":false,"isFork":false,"isEmpty":false,"createdAt":"2023-07-10T21:59:21.000+05:30","ownerAvatar":"https://avatars.githubusercontent.com/u/113445140?v=4","public":true,"private":false,"isOrgOwned":false},"symbolsExpanded":false,"treeExpanded":true,"refInfo":{"name":"main","listCacheKey":"v0:1691938061.0","canEdit":true,"refType":"branch","currentOid":"8903211aceb994e588d3984da88f6a675bda04fb"},"path":"R lab programs/Ex 35. to load the dataset.R","currentUser":{"id":100275995,"login":"sakthivelg007","userEmail":"svel56937@gmail.com"},"blob":{"rawLines":["# Load the required library\r","library(reshape2)\r","\r","# Load the ChickWeight dataset\r","data(ChickWeight)\r","\r","# (i) Order the data frame by \"weight\" in ascending order, grouped by \"diet\",\r","# and extract the last 6 records\r","ordered_data <- ChickWeight[order(ChickWeight$weight), ]\r","last_6_records <- tail(ordered_data, 6)\r","print(last_6_records)\r","\r","# (ii) (a) Melt the data based on \"Chick\", \"Time\", and \"Diet\" as ID variables\r","melted_data <- melt(ChickWeight, id.vars = c(\"Chick\", \"Time\", \"Diet\"))\r","\r","# (ii) (b) Cast function to display the mean value of weight grouped by Diet\r","mean_weight_by_diet <- dcast(melted_data, Diet ~ variable, fun.aggregate = mean)\r","print(mean_weight_by_diet)\r","\r","# (ii) (c) Calculate the mode function\r","calculate_mode <- function(x) {\r","  tbl <- table(x)\r","  mode_val <- as.numeric(names(tbl[tbl == max(tbl)]))\r","  return(mode_val)\r","}\r","\r","# Apply the mode calculation to melted_data and cast the results\r","mode_weight_by_diet <- dcast(melted_data, Diet ~ variable, fun.aggregate = calculate_mode)\r","print(mode_weight_by_diet)\r"],"stylingDirectives":[[{"start":0,"end":27,"cssClass":"pl-c"},{"start":0,"end":1,"cssClass":"pl-c"}],[{"start":8,"end":16,"cssClass":"pl-smi"}],[],[{"start":0,"end":30,"cssClass":"pl-c"},{"start":0,"end":1,"cssClass":"pl-c"}],[{"start":5