ramming","ownerLogin":"gokul-chandrasekar","currentUserCanPush":false,"isFork":false,"isEmpty":false,"createdAt":"2023-07-10T21:59:21.000+05:30","ownerAvatar":"https://avatars.githubusercontent.com/u/113445140?v=4","public":true,"private":false,"isOrgOwned":false},"symbolsExpanded":false,"treeExpanded":true,"refInfo":{"name":"main","listCacheKey":"v0:1691938061.0","canEdit":true,"refType":"branch","currentOid":"8903211aceb994e588d3984da88f6a675bda04fb"},"path":"R lab programs/Ex 38. built in histo dataset.R","currentUser":{"id":100275995,"login":"sakthivelg007","userEmail":"svel56937@gmail.com"},"blob":{"rawLines":["# Load the required library\r","library(ggplot2)\r","library(dplyr)\r","\r","# Load the Titanic dataset\r","data(Titanic)\r","\r","# Convert Titanic table to a data.frame\r","titanic_df <- as.data.frame(Titanic)\r","\r","# (a) Draw a Bar chart to show details of \"Survived\" on the Titanic based on passenger Class\r","barplot_class_survived <- ggplot(titanic_df, aes(x = Class, fill = factor(Survived))) +\r","  geom_bar(position = \"stack\") +\r","  labs(title = \"Survival Status by Passenger Class\",\r","       x = \"Passenger Class\",\r","       y = \"Count\",\r","       fill = \"Survived\")\r","\r","# (b) Modify the above plot based on gender of people who survived\r","barplot_class_gender_survived <- ggplot(titanic_df, aes(x = Class, fill = factor(Survived))) +\r","  geom_bar(position = \"stack\") +\r","  facet_grid(. ~ Sex) +\r","  labs(title = \"Survival Status by Passenger Class and Gender\",\r","       x = \"Passenger Class\",\r","       y = \"Count\",\r","       fill = \"Survived\")\r","\r","# (c) Draw bar plot to show distribution of feature \"Age\"\r","histogram_age <- ggplot(titanic_df, aes(x = Age)) +\r","  geom_bar(binwidth = 5, fill = \"skyblue\", color = \"black\", stat = \"count\") +\r","  labs(title = \"Distribution of Age\",\r","       x = \"Age\",\r","       y = \"Count\")\r","\r","# Display the plots\r","print(barplot_class_survived)\